import React, { Component } from 'react'

export default class AddTodo extends Component {
    state = { 
            content: ''
        }

    handleChange = (e) => {
        this.setState({
            content: e.target.value 
        })
    }

    handleSubmit = (e) => { 
        e.preventDefault(); 
        this.props.addTodo(this.state);


    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label>Add new todo:</label>
                    <input type="text" onChange={this.handleChange} value={this.state.content}></input> {/* is going to fire when an user types inside this input field */}
                </form>
            </div>
        )
    }
}
